import { NotificationError } from "../../../common/component/notificationError";

const setUser = payload => ({
    type: actionType.USER_DATA,
    payload,
  });

const setTitle = payload => ({
    type: actionType.SET_PAGE,
    payload,
  });

export const getUser = () => dispatch => 
new Promise((resolve, reject) => {
    dispatch(startLoadingAction());
    let res=[];
        let list=[];
        axios.get('https://panorbit.in/api/users.json')
        .then((response) => {  
        list= response.data.users.filter(item => item.id != this.props.match.params.id)  
        res = response.data.users.filter(item => item.id == this.props.match.params.id)
        }).then(()=>{
            this.setState({
                data: res[0],
                list,
            })
            resolve(data, list)
        }),
        (error) => {
            dispatch(finishLoadingAction());
            dispatch(NotificationError());
            reject(error);
        }
    });

export const getTitle = () => dispatch => new Promise((resolve, reject) => {
    dispatch(startLoadingAction());
    let res=[];
        let list=[];
        axios.get('https://panorbit.in/api/users.json')
        .then((response) => {  
        list= response.data.users.filter(item => item.id != this.props.match.params.id)  
        res = response.data.users.filter(item => item.id == this.props.match.params.id)
        }).then(()=>{
            this.setState({
                data: res[0],
                list,
            })
        }),
        (error) => {
            dispatch(finishLoadingAction());
            dispatch(NotificationError());
            reject(error);
        }
});
