class Authentication {
    constructor(){
        this.authenticated = false;
    }
login(fun) {
    this.authenticated = true;
    fun();
}    
logout(fun){
    this.authenticated = false;
    fun();
}
isAuthenticated(){
    if (this.authenticated) {
        return true;
    }
    return false;
}
}
export default new Authentication;
