    //     resolve(data, list)
    //     }),
    //     (error) => {
    //         // dispatch(finishLoadingAction());
    //         dispatch(NotificationError());
    //         reject(error);
    //     }
        axios.get("https://my-json-server.typicode.com/dpatil2011/jsonServer/members").then(
          (response) => {
            dispatch(setListUsers(response.data));
            resolve(response.data);
          },
          (error) => {
            alert(error);
            reject();
          }
        );
    });

export const getTitle = (params) => dispatch => new Promise((resolve, reject) => {
    // dispatch(startLoadingAction());
    let res=[];
        let list=[];
        axios.get(
            'https://panorbit.in/api/users.json',
            params
            )
        .then((response) => {  
        list= response.data.users.filter(item => item.id != this.props.match.params.id)  
        res = response.data.users.filter(item => item.id == this.props.match.params.id)
        }).then(()=>{
            this.setState({
                data: res[0],
                list,
            })
        }),
        (error) => {
            // dispatch(finishLoadingAction());
            dispatch(NotificationError());
            reject(error);
        }
});
