import moment from 'moment';
import * as actionType from './action-type';

const initialFormData = {
    firstName: '',
    branchName: '',
    address: '',
    address2: '',
    city: '',
    parent: '',
};
const initialDetailFormData = {
    code: '',
    branchName: '',
    address: '',
    address2: '',
    city: '',
    parent: '',
};

const initialState = {
    loading: false,
    dataAdd: initialFormData,
    dataEdit: initialDetailFormData,
    listBranch: [],
};

const handler = (currentState) => {
    const startLoading = () => ({ ...currentState, loading: true });
    const finishLoading = () => ({ ...currentState, loading: false });
    const setListBranch = payload => ({ ...currentState, listBranch: payload });
    const setDetailBranch = payload => ({ ...currentState, dataEdit: payload });
    return {
        startLoading,
        finishLoading,
        setListBranch,
        setDetailBranch,
    };
};

export default (state = initialState, action) => {
    const { payload, type } = action;
    switch (type) {
        case actionType.START_FETCH_DATA:
            return handler(state).startLoading();
        case actionType.FINISH_FETCH_DATA:
            return handler(state).finishLoading();
        case actionType.SET_LIST_BRANCH:
            return handler(state).setListBranch(payload);
        case actionType.SET_DETAIL_BRANCH:
            return handler(state).setDetailBranch(payload);
        default:
            return state;
    }
};
