import React, { Component, Fragment } from 'react';
import Home from './../modules/home-component';
import { connect } from 'react-redux';
import {getUser, setTitle} from './../common/store/action';
import axios from 'axios';

class homePage extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            title: "Profile",
            data: {
                "id": 1,
                "name": "Leanne Graham",
                "username": "Bret",
                "email": "Sincere@april.biz",
                "profilepicture": "https://panorbit.in/wp-content/uploads/2019/hotlink-ok/1001.jpeg",
                "address": {
                    "street": "Kulas Light",
                    "suite": "Apt. 556",
                    "city": "Gwenborough",
                    "zipcode": "92998-3874",
                    "geo": {
                        "lat": "-37.3159",
                        "lng": "81.1496",
                    }
                },
                "phone": "1-770-736-8031 x56442",
                "website": "hildegard.org",
                "company": {
                    "name": "Romaguera-Crona",
                    "catchPhrase": "Multi-layered client-server neural-net",
                    "bs": "harness real-time e-markets"
                }
            },
            list:[
                {
                    "id": 1,
                    "name": "Leanne Graham",
                    "username": "Bret",
                    "email": "Sincere@april.biz",
                    "profilepicture": "https://panorbit.in/wp-content/uploads/2019/hotlink-ok/1001.jpeg",
                    "address": {
                        "street": "Kulas Light",
                        "suite": "Apt. 556",
                        "city": "Gwenborough",
                        "zipcode": "92998-3874",
                        "geo": {
                            "lat": "-37.3159",
                            "lng": "81.1496",
                        }
                    },
                    "phone": "1-770-736-8031 x56442",
                    "website": "hildegard.org",
                    "company": {
                        "name": "Romaguera-Crona",
                        "catchPhrase": "Multi-layered client-server neural-net",
                        "bs": "harness real-time e-markets"
                    }
                }
            ]
         };
    }
    componentWillMount() {
        let res=[];
        let list=[];
        axios.get('https://panorbit.in/api/users.json')
        .then((response) => {  
        list= response.data.users.filter(item => item.id != this.props.match.params.id)  
        res = response.data.users.filter(item => item.id == this.props.match.params.id)
        }).then(()=>{
            this.setState({
                data: res[0],
                list,
            })
        });
    };
    onClickProfile = ((value) =>{
       this.props.setTitle(value)
    })    
   
    render() {
        return (
            <Fragment>
                <Home 
                title={this.props.login.title} 
                data={this.state.data}
                list={this.state.list}
                onClickProfile={this.onClickProfile}
                onClickGallery={this.onClickGallery}
                onClickPosts={this.onClickPosts}
                onClickTodo={this.onClickTodo}
                />
            </Fragment>
        );
    }
}

const mapStateToProps = (state) => {
    const { login } = state;
    return {
        login,
    };
  };
const mapDispatchToProps = (dispatch => ({
        getUser,
        setTitle,
    }))()
export default connect(mapStateToProps, mapDispatchToProps)(homePage);
