export const getUser = (service, params) => dispatch => new Promise((resolve, reject) => {
    dispatch(startLoadingAction());
    service.invoke(
        'api/cenco/cenco/data/table',
        params,
        {
            method: 'POST',
            header: {
                'Content-type': 'application/json',
            },
            APPS: APPS_BE,
        },

    ).then(
        (response) => {
            if (response.data.responseCode == 1000) {
                const listBranch = response.data.response.filter(val => (val.status === 'LIVE' && val.client_id === CLIENT_ID_TPE) || (val.status === 'INAU' && val.authoress && val.client_id === CLIENT_ID_TPE));
                const listTempBranch = listBranch.map((item) => {
                    return {
                        ...item,
                        branch_name: `${item.branch_code} - ${item.name}`,
                    };
                });
                resolve(listTempBranch);
                dispatch(setListData(listTempBranch));
                dispatch(finishLoadingAction());
            } else {
                dispatch(finishLoadingAction());
                resolve([]);
            }
        },
        (error) => {
            dispatch(finishLoadingAction());
            dispatch(showError(error.data.responseCode));
            reject(error);
        },
    );
});
